def support_external_local(self, path):
    """Support external local deps/outputs by adding to local cache."""
    if os.path.exists(path) and not os.path.isabs(path):
        # Convert relative path to absolute
        path = os.path.abspath(path)

    # Ensure path is in local cache
    if not self.local.exists(path):
        # Add to local cache if not already present
        self.local.add(path)

    return path
